import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View , ScrollView, TouchableOpacity} from 'react-native';
import {LinearGradient} from 'expo-linear-gradient';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import HeaderHome from './HeaderHome';
import {connect} from 'react-redux';
import CartCard from './CartCard';

class Panier extends React.Component{
  constructor(props){
    super(props)
  }
  render(){
    return (
      <View style={styles.container}>
        <StatusBar style="auto" />
        <HeaderHome title='Panier' onPress1={() => this.props.navigation.openDrawer()} />
        <ScrollView style={styles.content}>
          {this.props.cartItems.length > 0 ?
            <View>
              <CartCard title={'title'} subtitle={'subtitle'} price={'price'} onPress1={this.props.removeItem}/> 

            </View>
          :
          null
          }
        <View style={styles.totalSection}>
          <Text style={styles.title}>Totals</Text>
          <View style={styles.subTitles}>
            <Text style={styles.subtitlesText}>Sub Total</Text>
            <View style={styles.divider}/>
            <Text style={styles.price}>30.00 MAD</Text>
          </View>
          <View style={styles.subTitles}>
            <Text style={styles.subtitlesText}>Livraison</Text>
            <View style={styles.divider}/>
            <Text style={styles.gratuite}>Gratuite</Text>
          </View>
        </View>
        <TouchableOpacity onPress={() => alert("formulaire")} >
          <LinearGradient colors={['rgb(50,205,50)', 'green']} style={styles.button}>
            <Text style={styles.buttonText}>Finaliser La Commande</Text>
          </LinearGradient>
        </TouchableOpacity>
        </ScrollView>
      </View>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    cartItems: state
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
    removeItem: (product) => dispatch({type: 'REMOVE_FROM_CART',payload:product})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Panier);

const styles = ScaledSheet.create({
  container: {
    flex: 1,
  },

  content:{
    marginHorizontal: 29,
    paddingBottom: 32
  },

  totalSection:{
    marginTop: 32
  },

  title:{
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: 31,
    marginVertical: 12
  },

  divider:{
    height: 1,
    borderColor: '#dddddd',
    borderWidth: StyleSheet.hairlineWidth,
    flex: 1,
    marginHorizontal: 16,
    marginTop: 5
  },

  subTitles:{
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginVertical: 16
  },

  subtitlesText:{
    fontSize: 16,
    color: '#333333'
  },

  price:{
    fontSize: 16,
    fontWeight: 'bold'
  },

  gratuite:{
    fontSize: 18,
    fontWeight: 'bold',
    color: 'green'
  },

  button: {
    width: wp("48%"),
    height: hp("4%"),
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: hp("0.5%"),
  },

  buttonText:{
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: '16@s',
    color: '#fff'
  }


});
