import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { SafeAreaView, StyleSheet, Text, View, Image, TouchableOpacity, TouchableHighlight, Dimensions, TextInput, ScrollView} from 'react-native';
import { ScaledSheet, s, vs, ms, mvs } from 'react-native-size-matters';
import { widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';
import Feather from 'react-native-vector-icons/Feather';
import Entypo from 'react-native-vector-icons/Entypo';
import AntDesign from 'react-native-vector-icons/AntDesign';
import Animated ,{ Easing } from 'react-native-reanimated';
import {
  Box,
  FlatList,
  Heading,
  Avatar,
  HStack,
  VStack,
  Spacer,
  NativeBaseProvider,
  Center
} from "native-base";
import _ from 'lodash';
import {connect} from 'react-redux';
import cartItems from './reducers/cartItems'

const { Value, timing } = Animated;
const width = Dimensions.get('window').width;
const height = Dimensions.get('window').height;

class Header extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      isFocused: false,
      query: '',
      data: [
        {
          fullName: "Doliprane",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
        },
        {
          fullName: "Aspejic",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyEaZqT3fHeNrPGcnjLLX1v_W4mvBlgpwxnA&usqp=CAU",
        },
        {
          fullName: "Renumisin",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl: "https://miro.medium.com/max/1400/0*0fClPmIScV5pTLoE.jpg",
        },
        {
          fullName: "Idole",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr01zI37DYuR8bMV5exWQBSw28C1v_71CAh8d7GP1mplcmTgQA6Q66Oo--QedAN1B4E1k&usqp=CAU",
        },
        {
          fullName: "Protine",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBwgu1A5zgPSvfE83nurkuzNEoXs9DMNr8Ww&usqp=CAU",
        },
      ],
      fullData: [
        {
          fullName: "Doliprane",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
        },
        {
          fullName: "Aspejic",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyEaZqT3fHeNrPGcnjLLX1v_W4mvBlgpwxnA&usqp=CAU",
        },
        {
          fullName: "Renumisin",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl: "https://miro.medium.com/max/1400/0*0fClPmIScV5pTLoE.jpg",
        },
        {
          fullName: "Idole",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr01zI37DYuR8bMV5exWQBSw28C1v_71CAh8d7GP1mplcmTgQA6Q66Oo--QedAN1B4E1k&usqp=CAU",
        },
        {
          fullName: "Protine",
          price: "Prix: 12 MAD",
          recentText: "info",
          avatarUrl:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBwgu1A5zgPSvfE83nurkuzNEoXs9DMNr8Ww&usqp=CAU",
        },
      ]
    }
    this._input_box_translate_x = new Value(width)
    this._back_button_opacity = new Value(0)
    this._content_translate_y = new Value(height)
    this._content_opacity = new Value(0)
  }

  _onFocus = () => {

    this.setState({isFocused: true})

    const input_box_translate_x_config = {
      duration: 200, 
      toValue: 0,
       easing: Easing.inOut(Easing.ease)
    }

    const back_button_opacity_config = {
      duration: 200, 
      toValue: 1,
       easing: Easing.inOut(Easing.ease)
    }

    const content_translate_y_config = {
      duration: 0, 
      toValue: 0,
       easing: Easing.inOut(Easing.ease)
    }

    const content_opacity_config = {
      duration: 200, 
      toValue: 1,
       easing: Easing.inOut(Easing.ease)
    }

    timing(this._input_box_translate_x, input_box_translate_x_config).start()
    timing(this._back_button_opacity, back_button_opacity_config).start()
    timing(this._content_translate_y, content_translate_y_config).start()
    timing(this._content_opacity, content_opacity_config).start()

    this.refs.input.focus();

  }

  _onBlur = () => {

    this.setState({isFocused: false})

    const input_box_translate_x_config = {
      duration: 200, 
      toValue: width,
       easing: Easing.inOut(Easing.ease)
    }

    const back_button_opacity_config = {
      duration: 50, 
      toValue: 0,
       easing: Easing.inOut(Easing.ease)
    }

    const content_translate_y_config = {
      duration: 0, 
      toValue: height,
       easing: Easing.inOut(Easing.ease)
    }

    const content_opacity_config = {
      duration: 200, 
      toValue: 0,
       easing: Easing.inOut(Easing.ease)
    }

    timing(this._input_box_translate_x, input_box_translate_x_config).start()
    timing(this._back_button_opacity, back_button_opacity_config).start()
    timing(this._content_translate_y, content_translate_y_config).start()
    timing(this._content_opacity, content_opacity_config).start()

    this.refs.input.blur();
  }

  componentDidMount(){
    // this.requestData();
  }

  requestData = _.debounce(() => {
    const url = '';
    fetch(url)
    .then((res) => res.json())
    .then((resJson) => (
      this.setState({
        data: resJson,
        fullData: resJson
      })
    )).catch(error => {alert(error)})
  }, 250)

  handleSearch = (text) => {
    const formattedQuery = text;
    const data = _.filter(this.state.fullData, (text) => {
      if(text.fullName.includes(formattedQuery)) { 
        return true 
      }
      return false
    })
    this.setState({data, query: text})
  }

  // {/* <TouchableOpacity onPress={this.props.onPress}>
  //   <View style={styles.menuIcon} >
  //     <Entypo name="menu" size={35}  color="#FFF" />
  //   </View>
  // </TouchableOpacity>
  // <View style={styles.textContainer} >
  //   <Text style={styles.headerText} >{this.props.title}</Text>
  // </View> */}
  // {/* style={{marginTop: hp("3%"),justifyContent: 'space-between',alignItems: 'center',flexDirection: 'row'}} */}
  // {/* </View>
  // <View style={styles.searchContainer} > */}

  render() {

  return (
  <>
    <SafeAreaView style={styles.headerContainer}>
      <View style={{height: 50, paddingHorizontal: 16}}>
        <View style={styles.container}>
          <TouchableOpacity onPress={this.props.onPress1}>
            <View style={styles.menuIcon} >
