              </View>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </SafeAreaView>
    <Animated.View style={[styles.content, {opacity: this._content_opacity, transform: [{translateY: this._content_translate_y}]}]}>
      <SafeAreaView style={styles.contentSafeArea}> 
        <View style={styles.contentInner}>
          {/* {
