import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { Image, StyleSheet, Text, View, ScrollView, Linking} from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import Medicaments from './Medicaments';
import HeaderHome from './HeaderHome';
import Panier from './Panier';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import SimpleLineIcons from 'react-native-vector-icons/SimpleLineIcons';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import {LinearGradient} from 'expo-linear-gradient';
import Foundation from 'react-native-vector-icons/Foundation'
import MapViewDirections from 'react-native-maps-directions';
import MapView from 'react-native-maps';

export default class Pharmacie extends Component{
  constructor(props){
    super(props)
  }

  render(){ 
    const coordinates = [
      {
        latitude: 37.3318456, 
        longitude: -122.0296002
      },
      {
        latitude: 37.771707, 
        longitude: -122.4053769
      }
    ];
    const GOOGLE_MAPS_APIKEY = 'AIzaSyBCBH3GCpkRXHaPu1OUqxwytzMUYePVHZ4';

    const handleCallPress =() => {
      Linking.openURL("tel:+212625150645");
    }

    const scheme = Platform.select({ ios: 'maps:0,0?q=', android: 'geo:0,0?q=' });
    const latLng = `${30.41670036},${-9.60000038}`;
    const label = 'Pharmacie salam';
    const url = Platform.select({
      ios: `${scheme}${label}@${latLng}`,
      android: `${scheme}${latLng}(${label})`
    });

    const handleMapPress =() => {
      Linking.openURL(url);
    }

  return (
    <View style={styles.container}>
      <HeaderHome title={'Pharmacie'} onPress1={() => this.props.navigation.openDrawer()} onPress2={() => this.props.navigation.navigate("Panier")}/> 
      <View style={styles.view}>
        <ScrollView style={{backgroundColor: 'transparent'}}>
          <View style={styles.titleContainer}>
            <Text style={styles.titleText}>Pharmacie salam ait omghar </Text>
          </View>
          <View style={styles.imageContainer}>
            <Image
              source={require("../Images/pharmacy1.jpeg")}
              style={styles.image}
            />
          </View>
          <MapView initialRegion={{latitude: 37.3318456, longitude: -122.0296002}} style={styles.map} >
            <MapView.Marker coordinate={coordinates[0]}/>
            <MapView.Marker coordinate={coordinates[1]}/>
            <MapViewDirections
              origin={coordinates[0]}
              destination={coordinates[1]}
              apikey={GOOGLE_MAPS_APIKEY}
              strokeWidth={3}
              strokeColor='black'
            />
          </MapView>
          <TouchableOpacity onPress={handleMapPress} >
            <LinearGradient colors={['rgb(50,205,50)', 'green']} style={styles.mapContainer}>
              <Text style={styles.mapText}>Go to Maps...</Text>
            </LinearGradient>
          </TouchableOpacity>
          <View style={styles.adresseContainer}>
            <MaterialCommunityIcons name="google-maps" size={40} color='green' style={styles.mapIcon}/>
            <Text style={styles.adresseText}>biougra chtouka ait baha dayma hay touama </Text>
          </View>
          <TouchableOpacity onPress={handleCallPress}>
            <View style={styles.telContainer}>
              <Foundation name="telephone" size={40} color='green' style={styles.telIcon}/>
              <Text style={styles.telText}>+212606060606</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => this.props.navigation.navigate("Medicaments")} >
            <LinearGradient colors={['rgb(50,205,50)', 'green']} style={styles.medContainer}>
              <Text style={styles.medText}>Allez aux medicaments...</Text>
            </LinearGradient>
          </TouchableOpacity>
        </ScrollView>
      </View>
      <StatusBar style="auto" /> 
    </View>
  );
}
}

const styles = ScaledSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
  },

  view:{
    marginTop: hp("3%"),
    width: wp("95%"),
    height: hp("85%"),
    backgroundColor: '#e4ebe6',
    borderWidth: 2,
    borderColor: "green",
    borderRadius: 20,
  },

   map: {
    width: '90%',
    height: '30%',
    borderRadius: 10,
    marginRight: '4%',
    marginLeft: '5%',
  },

  imageContainer:{
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: hp("3%"),
    marginBottom: hp("1%"),
    shadowColor: "#000",
    shadowOffset: {
      width: '18@s',
      height: '17@vs',
    },
    shadowOpacity: 0.48,
    shadowRadius: 16.00,
    elevation: 24,
  },

  image:{
    width: wp("90%"),
    height: hp("25%"),
    borderWidth: 0,
    borderColor: "black",
    borderRadius: 20,
  },

  titleContainer:{
    // flexDirection: 'row',
    marginTop: hp("3%") ,
    marginBottom: hp("1%"),
    alignItems: 'center',
    justifyContent: 'center',
  },

  titleText:{
    fontSize: '26@s',
    fontWeight: 'bold',
    color: 'rgb(0,100,0)'
  },

  adresseContainer:{
    marginLeft: wp("3%"),
    marginTop: hp("3%")
  },

  adresseText:{
    fontFamily: 'Gill Sans',
    textAlign: 'left',
    marginTop: wp("2%"),
    marginLeft: wp("15%"),
    fontSize: '15@s',
    fontWeight: 'bold',
    color: 'green',
    paddingRight: wp("1%")
  },

  mapIcon:{
    position: 'absolute',
    marginLeft: wp("2%"),
    marginTop: wp("0.5%"),
  },

  mapContainer:{
    right: "-40%",
    width: wp("18%"),
    height: hp("3%"),
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
    marginTop: hp("1%")
  },

  mapText:{
    fontSize: '8@s',
    fontFamily: 'Gill Sans',
    textAlign: 'center',
    color: '#fff',
    backgroundColor: 'transparent',

  },

  telContainer:{
    // flexDirection: 'row',
    marginLeft: wp("3%"),
    marginBottom: hp("3%")
  },

  telText:{
    fontFamily: 'Gill Sans',
    textAlign: 'left',
    marginTop: wp("6%"),
    marginLeft: wp("15%"),
    fontSize: '15@s',
    fontWeight: 'bold',
    color: 'green',
    paddingRight: wp("1%")
  },

  telIcon:{
    position: 'absolute',
    marginLeft: wp("3%"),
    marginTop: wp("2.5%"),
  },

  medContainer:{
    right: "-54%",
    width: wp("38%"),
    height: hp("4%"),
    borderRadius: 5,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: hp("2%")
  },

  medText:{
    fontSize: '14@s',
    fontFamily: 'Gill Sans',
    textAlign: 'center',
    color: '#fff',
    backgroundColor: 'transparent',
  },

});
